// OrderCard.js
// A simple component to present a minimal order form for a service.  Since
// the full ordering workflow is not yet implemented, this card provides a
// placeholder interface with a quantity input and a button that displays
// a friendly message encouraging the user to contact a manager for a
// detailed order.  The card accepts an optional serviceTitle prop for
// future extensibility (e.g., passing the current service name), though
// it is not currently displayed.  Styling is applied via the global
// stylesheet under the `.order-card` class.

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const OrderCard = ({ serviceTitle }) => {
  const [quantity, setQuantity] = useState(1);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const navigate = useNavigate();

  /**
   * Called when the user selects files.  The input element returns a
   * FileList which we convert to an array for easier handling.  We
   * deliberately allow multiple files to support orders containing
   * several images (e.g. multiple A4 prints at once).
   */
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files || []);
    setSelectedFiles(files);
  };

  /**
   * Submit the order to the backend.  We construct a FormData object
   * containing the selected files, quantity and service title.  After
   * successfully posting the data, we navigate to the payment page.
   */
  const handleOrder = async () => {
    try {
      const formData = new FormData();
      selectedFiles.forEach((file) => formData.append('files', file));
      formData.append('quantity', quantity);
      if (serviceTitle) {
        formData.append('service', serviceTitle);
      }

      const response = await fetch('http://localhost:5000/api/orders', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) {
        throw new Error('Ошибка отправки заказа');
      }
      const data = await response.json();
      // Optionally use data.orderId or similar
      navigate('/payment');
    } catch (error) {
      console.error(error);
      alert(
        'Произошла ошибка при оформлении заказа. Пожалуйста, попробуйте позже или свяжитесь с менеджером в Telegram: @aiprintperm'
      );
    }
  };

  return (
    <div className="order-card">
      <h3>Быстрый заказ</h3>
      <p>Укажите количество, добавьте файлы и нажмите «Оформить заказ».</p>
      <div className="order-form">
        <label style={{ display: 'block', marginBottom: '0.5rem' }}>
          Загрузите файлы:
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={handleFileChange}
          />
        </label>
        {/* Display selected file names as a list */}
        {selectedFiles.length > 0 && (
          <ul className="selected-files">
            {selectedFiles.map((file, idx) => (
              <li key={idx} style={{ fontSize: '0.8rem', color: '#cccccc' }}>
                {file.name}
              </li>
            ))}
          </ul>
        )}
        <label style={{ marginTop: '0.5rem', display: 'block' }}>
          Количество:
          <input
            type="number"
            min="1"
            value={quantity}
            onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
          />
        </label>
      </div>
      <button className="btn btn-primary" onClick={handleOrder} style={{ marginTop: '1rem' }}>
        Оформить заказ
      </button>
    </div>
  );
};

export default OrderCard;
