// Services.js
// Displays a list of services offered by AI Print Studio. The service
// information is fetched from the backend API. If the API is unavailable
// (for example during initial development), a static fallback list is
// provided. Categories are based on the business listings: photo centre,
// copy services, operational and offset printing, and advertising design【339219316284430†L95-L101】.

import React, { useEffect, useState } from 'react';
import ServiceCard from '../components/ServiceCard';

const Services = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Attempt to fetch service list from the backend. If it fails,
    // fallback to a hardcoded list so the page still displays content.
    const fetchServices = async () => {
      try {
        const response = await fetch('/api/services');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setServices(data);
      } catch (err) {
        console.error('Fetching services failed:', err);
        // Fallback services list based on published categories and common
        // printing offerings.
        setServices([
          {
            title: 'Печать фотографий',
            description:
              'Высококачественная печать фотографий любого формата. Быстрая печать полароид‑фотографий упоминается в описании профиля компании на поиске【958412501063179†L1-L3】.',
          },
          {
            title: 'Копировальные услуги и сканирование',
            description:
              'Чёрно‑белое и цветное копирование документов, сканирование и цифровой архив.',
          },
          {
            title: 'Оперативная и офсетная полиграфия',
            description:
              'Печать визиток, листовок, брошюр, сертификатов. Оперативная полиграфия и офсетная печать указаны в рубриках【339219316284430†L95-L101】.',
          },
          {
            title: 'Дизайн и реклама',
            description:
              'Разработка макетов и рекламных материалов для вашего бизнеса, брендинг, фирменный стиль.',
          },
          {
            title: 'Печать на одежде и мерче',
            description:
              'Нанесение принтов, логотипов и надписей на футболки, худи и другой текстиль. Собственное производство позволяет контролировать качество.',
          },
          {
            title: 'Онлайн‑услуги и доставка',
            description:
              'Вам не нужно приезжать в офис: отправляйте файлы онлайн, оплачивайте любым удобным способом и получите готовый заказ с доставкой【339219316284430†L104-L107】.',
          },
        ]);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchServices();
  }, []);

  return (
    <div className="services">
      <h1>Наши услуги</h1>
      {loading ? (
        <p>Загрузка...</p>
      ) : (
        <div className="service-list">
          {services.map((service, index) => (
            <ServiceCard
              key={index}
              title={service.title}
              description={service.description}
            />
          ))}
        </div>
      )}
      {error && (
        <p className="error">Не удалось загрузить данные с сервера: {error}</p>
      )}
    </div>
  );
};

export default Services;
